<?php$host="localhost";$username="root";$password="";$db_name="facebook";$tbl_name="status";$con=mysqli_connect("$host","$username","$password","$db_name");$text=$_GET['msg'];$display="";if($text!=""){$query="insert into status(Status) values('$text')";mysqli_query($con,$query);}$qry="select * from status order by ID desc";$result=mysqli_query($con,$qry);while($row = mysqli_fetch_array($result)){$display = $display."<div>".$row["Status"]."<br><br><div>";}echo $display;mysqli_close($con);?>