<?php$host="localhost";$username="root";$password="";$db_name="facebook";$tbl_name="status";$con=mysqli_connect("$host","$username","$password","$db_name");$text=$_GET['msg'];$display="";if($text!=""){	$query="insert into status(Status) values('$text')";	mysqli_query($con,$query);	$query="select MAX(ID)as ID from status";	$result=mysqli_query($con,$query);	while($row = mysqli_fetch_array($result))	{		$display = $row["ID"];			}	echo $display;	}mysqli_close($con);?>